import{_ as o,c as i,o as s,ah as t}from"./chunks/framework.PvdZnrZd.js";const p=JSON.parse('{"title":"Commission Overview","description":"Commission Overview","frontmatter":{"title":"Commission Overview","description":"Commission Overview","author":"Chirag Tyagi"},"headers":[],"relativePath":"zenith/commission/overview.md","filePath":"zenith/commission/overview.md"}'),r={name:"zenith/commission/overview.md"};function n(a,e,l,m,c,h){return s(),i("div",null,[...e[0]||(e[0]=[t('<h2 id="commission-overview-–-shopify-multivendor-marketplace-app" tabindex="-1">Commission Overview – Shopify Multivendor Marketplace App <a class="header-anchor" href="#commission-overview-–-shopify-multivendor-marketplace-app" aria-label="Permalink to &quot;Commission Overview – Shopify Multivendor Marketplace App&quot;">​</a></h2><p><a href="/zenith/introduction/">Multivendor Marketplace for Shopify</a> offers a robust <strong>commission management system</strong>, allowing store owners to earn from every sale made by their sellers.</p><p>In a marketplace model, <strong>commissions</strong> are the core of the revenue structure for the admin. They represent the amount earned by the marketplace owner from each transaction made by vendors.</p><h2 id="what-is-a-commission" tabindex="-1">What is a Commission? <a class="header-anchor" href="#what-is-a-commission" aria-label="Permalink to &quot;What is a Commission?&quot;">​</a></h2><p>A <strong>commission</strong> is the percentage or fixed fee that the <strong>admin (store owner)</strong> earns from a seller&#39;s product sale. It can be configured in multiple ways based on your business model:</p><ul><li><strong>Global Commission</strong></li><li><strong>Seller-wise Commission</strong></li><li><strong>Product-wise Commission</strong></li><li><strong>Category/Collection-wise Commission</strong></li><li><strong>Variant-wise Commission</strong></li></ul><blockquote><p>Example:<br> If Seller X lists Product A for $100 and the admin sets a 10% commission, then:</p><ul><li>Admin earns <strong>$10</strong></li><li>Seller receives <strong>$90</strong></li></ul></blockquote><p>The app follows this <strong>commission priority hierarchy</strong>:<br><code>Global Commission &lt; Seller-wise &lt; Category-wise &lt; Product-wise</code></p><p><a href="https://youtu.be/TjXDS7dG3jA" target="_blank" rel="noreferrer">Watch the video guide on setting commissions</a></p><hr><h2 id="global-commission" tabindex="-1">Global Commission <a class="header-anchor" href="#global-commission" aria-label="Permalink to &quot;Global Commission&quot;">​</a></h2><ul><li>Applied to all sellers, products, and collections.</li><li>Set from the <strong>Global Commission Settings</strong> tab in the app.</li><li>Supports both <strong>percentage-based</strong> and <strong>fixed-value</strong> commission.</li></ul><p><strong>Example:</strong></p><p>If commission is set to <strong>10% + $20</strong>, and the total order is $1400:</p><ul><li>Seller A’s sale: $500 → Admin gets $70 → Seller earns $430</li><li>Seller B’s sale: $900 → Admin gets $110 → Seller earns $790</li></ul><hr><h2 id="seller-wise-commission" tabindex="-1">Seller-wise Commission <a class="header-anchor" href="#seller-wise-commission" aria-label="Permalink to &quot;Seller-wise Commission&quot;">​</a></h2><p>You can override the global settings for specific sellers from the <strong>Seller Commission Settings</strong> page in the app.</p><p><img src="https://cdnblog.webkul.com/blog/wp-content/uploads/2025/09/comm1.webp" alt="Seller Commission Settings"></p><hr><h2 id="product-wise-commission" tabindex="-1">Product-wise Commission <a class="header-anchor" href="#product-wise-commission" aria-label="Permalink to &quot;Product-wise Commission&quot;">​</a></h2><p>Enable product-wise commission from the app’s configuration settings.<br> Navigate to <strong>Products &gt; Product Listing &gt; Edit Product</strong> and set the desired commission.</p><blockquote><p>Note: Disable “Display Sales Price on Products” from the configuration to use this.</p></blockquote><hr><h2 id="category-collection-wise-commission" tabindex="-1">Category (Collection)-wise Commission <a class="header-anchor" href="#category-collection-wise-commission" aria-label="Permalink to &quot;Category (Collection)-wise Commission&quot;">​</a></h2><p>To enable this:</p><ol><li>Activate “Restrict Collection” from the configuration.</li><li>Go to <strong>Products &gt; Collections</strong> and click <strong>Sync</strong>.</li><li>Set the commission via the <strong>Collection Commission Settings</strong> page.</li></ol><hr><h2 id="variant-wise-commission" tabindex="-1">Variant-wise Commission <a class="header-anchor" href="#variant-wise-commission" aria-label="Permalink to &quot;Variant-wise Commission&quot;">​</a></h2><p>Commission can be applied at the <strong>variant level</strong> by navigating to: <code>Products &gt; Product Listing &gt; Edit Product &gt; Edit Variant</code></p><p>Set the commission on individual variants as needed.</p><hr><h2 id="seller-panel-–-commission-listing" tabindex="-1">Seller Panel – Commission Listing <a class="header-anchor" href="#seller-panel-–-commission-listing" aria-label="Permalink to &quot;Seller Panel – Commission Listing&quot;">​</a></h2><p>Sellers can view commissions for each order from:<br><strong>Seller Panel &gt; Orders &gt; Commission Listing</strong></p><p><img src="https://cdnblog.webkul.com/blog/wp-content/uploads/2025/09/commi2.webp" alt="Seller Commission View"></p><hr><h3 id="learn-more" tabindex="-1">Learn More <a class="header-anchor" href="#learn-more" aria-label="Permalink to &quot;Learn More&quot;">​</a></h3><p><a href="/zenith/commission/commission-type.html">Read the full guide on Commission Types</a></p><h3 id="demo" tabindex="-1">Demo <a class="header-anchor" href="#demo" aria-label="Permalink to &quot;Demo&quot;">​</a></h3><p>Explore a live demo of the Multivendor Marketplace:<br><a href="https://multivendor-marketplace-5.myshopify.com/" target="_blank" rel="noreferrer">https://multivendor-marketplace-5.myshopify.com/</a></p><hr>',41)])])}const g=o(r,[["render",n]]);export{p as __pageData,g as default};
